INSERT INTO [dbo].[Recipe] ([Id], [Title], [Instructions], [RecipeImageLocation], [Price]) VALUES (1, N'Instant frozen berry yogurt', N'Ingredients: 250g frozen mixed berry 250g 0%-fat Greek yogurt 1 tbsp honey or agave syrup <br>  <br> Blend berries, yogurt and honey or agave syrup in a food processor for 20 seconds, until it comes together to a smooth ice-cream texture. Scoop into bowls and serve.  ', N'strawberry_yogurt.jpg', CAST(20.00 AS Decimal(18, 2)))
INSERT INTO [dbo].[Recipe] ([Id], [Title], [Instructions], [RecipeImageLocation], [Price]) VALUES (2, N'Breakfast super-shake', N'Ingredients 100ml full-fat milk  2 tbsp natural yogurt 1 banana  150g frozen fruits of the forest 50g blueberries  1 tbsp chia seeds ½ tsp cinnamon  1 tbsp goji berries  1 tsp mixed seeds 1 tsp honey   (ideally Manuka)  <br> <br> Put the ingredients in a blender and blitz until smooth. Pour into a glass and enjoy!', N'breakfast-super-shake.jpg', CAST(15.25 AS Decimal(18, 2)))
INSERT INTO [dbo].[Recipe] ([Id], [Title], [Instructions], [RecipeImageLocation], [Price]) VALUES (3, N'Chorizo bean burgers', N'Ingredients 100g mini cooking chorizo  , skins removed 3 pork sausages, squeezed from their skins ¼ tsp smoked paprika 1 tbsp lime juice, plus wedges to serve small bunch coriander, chopped ½ x 400g can mixed beans, drained and rinsed 1 medium egg, lightly whisked 1½ tbsp olive oil  , plus extra to drizzle 2 ciabatta rolls, split and toasted soured cream, avocado, small handful rocket and chips, to serve (optional)  <br>  <br>   In a food processor, pulse the chorizo, sausagemeat, paprika, lime juice and coriander until well combined. Add the beans and some seasoning, and quickly pulse again to mix. Tip into a bowl and mix in just enough egg (about half) to bind. Shape into 2 fat burgers, cover and chill for 15 mins. Meanwhile, heat oven to 200C/180C fan/gas 6.  Heat the oil in a pan and fry the burgers for 3 mins on each side. Transfer to a baking sheet and cook in the oven for a further 10 mins until cooked through.  Serve the burgers in rolls with soured cream, avocado and rocket, with chips and lime wedges on the side, if you like.', N'bean_burguer.jpg', CAST(25.75 AS Decimal(18, 2)))
